import React, { Component } from 'react';
import ls from "local-storage";

export default class Rprofile extends Component {

    constructor(props) {
        super(props);

        this.onChangeBio = this.onChangeBio.bind(this);
        this.onChangePhone = this.onChangePhone.bind(this);
        this.onSubmit = this.onSubmit.bind(this);

        this.state = {
            bio: '',
            phone: '',
        }
    }

    onChangeBio(e) {
        this.setState({
            bio: e.target.value
        })
    }
    onChangePhone(e) {
        this.setState({
            phone: e.target.value
        })
    }

    componentDidMount() {
        const newJson = {
            email: ls.get("email"),
        }
        axios.post('http://localhost:4000/profile/rget', newJson)
            .then(res => {
                if(res.body.bio){
                    this.setState({
                        bio: res.body.bio
                    })
                }
                if(res.body.phone){
                    this.setState({
                        phone: res.body.phone
                    })
                }
            })

    }

    onSubmit(e) {
        e.preventDefault();

        const newJson = {
            email: ls.get("email"),
            bio: this.state.bio,
            phone: this.state.phone,
        }

        console.log(newJson);

        axios.post('http://localhost:4000/profile/redit', newJson)
            .then(res => console.log(res.data))
            .catch(error => console.log(error.response))
    }

    render() {
      return (
        <div>
            <h3> Recruiter Profile </h3>
            <form onSubmit={this.onSubmit}>
            <div className="form-group"> 
                <label>Name: </label>
                <input  type="text"
                    className="form-control"
                    value={ls.get("name")}
                    readonly
                    />
            </div>
            <div className="form-group"> 
                <label>Email: </label>
                <input  type="text"
                    className="form-control"
                    value={ls.get("email")}
                    readonly
                    />
            </div>
            <div className="form-group"> 
                <label>Bio: </label>
                <input  type="text"
                    required
                    className="form-control"
                    value={this.state.bio}
                    onChange={this.onChangeBio}
                    />
            </div>
            <div className="form-group"> 
                <label>Phone: </label>
                <input  type="text"
                    required
                    className="form-control"
                    value={this.state.phone}
                    onChange={this.onChangePhone}
                    />
            </div>
            <div className="form-group">
                <input type="submit" value="Update Profile" className="btn btn-primary" />
            </div>
            </form>
            </div>
      );
    }
  }
