import React, { Component } from 'react';
import axios from 'axios';
import ls from "local-storage";

export default class Aprofile extends Component {

    constructor(props) {
        super(props);

        this.onChangeEducation = this.onChangeEducation.bind(this);
        this.onChangeSkills = this.onChangeSkills.bind(this);
        this.onSubmit = this.onSubmit.bind(this);

        this.state = {
            education: '',
            skills: '',
        }
    }

    onChangeEducation(e) {
        this.setState({
            education: e.target.value
        })
    }
    onChangeSkills(e) {
        this.setState({
            skills: e.target.value
        })
    }

    componentDidMount() {
        const newJson = {
            email: ls.get("email"),
        }
        console.log(newJson);
        axios.post('/profile/rget', newJson)
            .then(response => {
                console.log(response)
                if(response.data.length>0)
                {
                    if(response.data[0].education){
                        this.setState({
                            education: response.data[0].education
                        })
                    }
                    if(response.data[0].skills){
                        this.setState({
                            skills: response.data[0].skills
                        })
                    }
                }
            })

    }

    onSubmit(e) {
        e.preventDefault();

        const newJson = {
            email: ls.get("email"),
            bio: this.state.bio,
            phone: this.state.phone,
        }

        console.log(newJson);

        axios.post('http://localhost:4000/profile/redit', newJson)
            .then(res => console.log(res.data))
            .catch(error => console.log(error.response));
    }

    render() {
      return (
        <div>
            <h3> Recruiter Profile </h3>
            <form onSubmit={this.onSubmit}>
            <div className="form-group"> 
                <label>Name: </label>
                <input  type="text"
                    className="form-control"
                    value={ls.get("name")}
                    readOnly
                    />
            </div>
            <div className="form-group"> 
                <label>Email: </label>
                <input  type="text"
                    className="form-control"
                    value={ls.get("email")}
                    readOnly
                    />
            </div>
            <div className="form-group"> 
                <label>Bio: </label>
                <input  type="text"
                    required
                    className="form-control"
                    value={this.state.bio}
                    onChange={this.onChangeEducation}
                    />
            </div>
            <div className="form-group"> 
                <label>Phone: </label>
                <input  type="text"
                    required
                    className="form-control"
                    value={this.state.phone}
                    onChange={this.onChangeSkills}
                    />
            </div>
            <div className="form-group">
                <input type="submit" value="Update Profile" className="btn btn-primary" />
            </div>
            </form>
            </div>
      );
    }
  }
