import React, { Component } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import Fuse from 'fuse.js';
import ls from "local-storage";

import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import Divider from '@material-ui/core/Divider';
import Autocomplete from '@material-ui/lab/Autocomplete';
import IconButton from "@material-ui/core/IconButton";
import InputAdornment from "@material-ui/core/InputAdornment";

import SearchIcon from "@material-ui/icons/Search";
import ArrowUpwardIcon from '@material-ui/icons/ArrowUpward';
import ArrowDownwardIcon from '@material-ui/icons/ArrowDownward';

export default class ListApplications extends Component {

	constructor(props) {
		super(props);

		this.state = {
            apps: [],
            ogApps: [],
            // sortSalary:true,
            // sortDuration:true,
            // minsal:'',
            // maxsal:'',
            // duration:'',
        };


    }

    fetch = async () => {

        const jobId = ls.get("jobId");
        const data = {jobId: jobId};

        try {
            const response = await axios.post('/jobs/jobapps', data);

            console.log(response.data);
            if(response.data){
                this.setState({apps: response.data, ogApps:response.data});
            }
            console.log(this.state.apps);

        } catch (error) {
            console.log(error);
        }        

    }

    componentDidMount() {
        this.fetch();
    }


	render() {
		return (
			<div>
                <Grid container>
                    <Grid item fluid>
                        <List component="nav" aria-label="mailbox folders">
                            <ListItem text>
                                <h3>Filters</h3>
                                <br/>
                                <Link to="#" className="btn-flat waves-effect" onClick={this.handleClearFilters}>
                                    Clear Filters
                                </Link>
                            </ListItem>
                        </List>
                    </Grid>
                </Grid>
                <Grid container>
                    <Grid item xs={12} md={3} lg={3}>
                        <List component="nav" aria-label="mailbox folders">

                            <ListItem button>
                                <form 
                                    // onSubmit={this.onSubmitFiltersal} 
                                    noValidate autoComplete="off">
                                    <label>Salary</label>
                                    <TextField id="standard-basic" label="Enter Min" fullWidth={true} 
                                        // value={this.state.minsal} 
                                        // onChange={this.onChangeMinsal}
                                    />
                                    <TextField id="standard-basic" label="Enter Max" fullWidth={true} 
                                        // value={this.state.maxsal} 
                                        // onChange={this.onChangeMaxsal}
                                    />
                                    <input type="submit" value="Filter Salary" className="btn btn-primary"/>
                                </form>                                                                
                            </ListItem>
                            <Divider />
                            <ListItem button>
                                <form 
                                    // onSubmit={this.onSubmitFilterdur} 
                                    noValidate autoComplete="off">
                                    <label>Duration</label>
                                    <TextField id="standard-basic" label="1-7 months" fullWidth={true} 
                                        // value={this.state.duration} 
                                        // onChange={this.onChangeDuration}
                                        />
                                    <input type="submit" value="Filter Duration" className="btn btn-primary"/>
                                </form>                                                                
                            </ListItem>
                        </List>
                    </Grid>
                    <Grid item xs={12} md={9} lg={9}>
                        <Paper>
                            <Table size="small">
                                <TableHead>
                                    <TableRow>
                                            <TableCell>Applicant Name</TableCell>
                                            <TableCell>Skills</TableCell>
                                            <TableCell>Date of Application</TableCell>
                                            <TableCell>Education</TableCell>
                                            <TableCell>SOP</TableCell>
                                            <TableCell>Stage</TableCell>
                                            {/* <TableCell>
                                                <Button onClick={this.sortChangeSal}>
                                                    {this.renderIconSal()}
                                                </Button>
                                                Salary
                                            </TableCell>
                                            <TableCell>
                                                <Button onClick={this.sortChangeDur}>
                                                    {this.renderIconDur()}
                                                </Button>
                                                Duration
                                            </TableCell> */}
                                            <TableCell>Accept</TableCell>
                                            <TableCell>Reject</TableCell>
                                    </TableRow>
                                </TableHead>
                                <TableBody>
                                    {this.state.apps.map( (app,ind) => (
                                        <TableRow key={ind}>
                                            <TableCell>{app.appId.name}</TableCell>
                                            <TableCell>{app.appId.aprofileId.skills}</TableCell>
                                            <TableCell>{app.date}</TableCell>
                                            <TableCell>{app.salary}</TableCell>
                                            <TableCell>{app.duration}</TableCell>
                                            <TableCell>
                                                <Button onClick={() => this.handleApply(job._id)}>
                                                    Apply
                                                </Button>
                                            </TableCell>
                                        </TableRow>
                                ))}
                                </TableBody>
                            </Table>
                        </Paper>               
                    </Grid>    
                </Grid>            
            </div>
		);
	}
}
