import React, { Component } from "react";
import axios from 'axios';
import ls from "local-storage";

import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import Divider from '@material-ui/core/Divider';
import Autocomplete from '@material-ui/lab/Autocomplete';
import IconButton from "@material-ui/core/IconButton";
import InputAdornment from "@material-ui/core/InputAdornment";

import SearchIcon from "@material-ui/icons/Search";
import ArrowUpwardIcon from '@material-ui/icons/ArrowUpward';
import ArrowDownwardIcon from '@material-ui/icons/ArrowDownward';

class MyApps extends Component {

    constructor(props) {
		super(props);

		this.state = {
            apps: [],
        };

        // this.sortChangeDur = this.sortChangeDur.bind(this);
	}

    componentDidMount() {
        axios.post('/myapps')
            .then(response => {
                this.setState({apps: response.data});
            })
            .catch(function(error) {
                console.log(error);
            })
    }

    // onChangeDuration(e) {this.setState({duration: e.target.value})};


    render() {
		return (
            <div>
                <Grid item xs={12} md={9} lg={9}>
                {/* <Grid > */}
                    <Paper>
                        <Table size="small">
                        {/* <Table> */}
                            <TableHead>
                                <TableRow>
                                        <TableCell>Title</TableCell>
                                        <TableCell>Date Of Joining</TableCell>
                                        <TableCell>Recruiter</TableCell>
                                        <TableCell>Salary</TableCell>
                                        <TableCell>Status</TableCell>
                                        <TableCell>Rate Job</TableCell>
                                </TableRow>
                            </TableHead>
                            <TableBody>
                                {this.state.apps.map( (job,ind) => (
                                    <TableRow key={ind}>
                                        <TableCell>{job.title}</TableCell>
                                        <TableCell>{job.jobtype}</TableCell>
                                        <TableCell>{job.rec_name}</TableCell>
                                        <TableCell>{job.salary}</TableCell>
                                        <TableCell>{job.duration}</TableCell>
                                        <TableCell>
                                            <Button onClick={() => this.handleApply(job._id)}>
                                                {/* {this.renderApplyState()} */}
                                                Apply
                                            </Button>
                                        </TableCell>
                                    </TableRow>
                            ))}
                            </TableBody>
                        </Table>
                    </Paper>               
                </Grid> 
            </div>
		);
	}
}
export default MyApps;
